<?phpfunction __autoload($class_name) {require_once "../../classes/".$class_name.".php"; }//автоподключение нужного класса$orderswitcher = new OrderSwitchers();//создание объекта класса OrderSwitchersforeach($_GET as $var=>$var_value)$orderswitcher->write($var, $var_value);//запись в БД свойств переключателя, введенных на предыдущей страницеsetcookie("switcher_handle", $orderswitcher->read("handle")); // запись в  cookie ручки 	if($orderswitcher->read("handle")== "ub" ||$orderswitcher->read("handle")== "ur" ){		$orderswitcher->write("board", 2);//запись в БД  таблички U		setcookie("switcher_inf_board", 2);//запись в cookie таблички U		header( 'Location: constr_switcher5.php', true, 303 );	}	else	if($orderswitcher->read("handle")== "key" ){		$orderswitcher->write("board", 0);//запись в БД отсутствие таблички		setcookie("switcher_inf_board", 0);//запись в cookie отсутствие таблички 		$orderswitcher->write("fix", "_");//запись в БД крепление на переднюю панель или дверь		header( 'Location: constr_switcher6.php', true, 303 );	}	?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.1//EN" "http://www.w3.org/TR/xhtml11/DTD/xhtml11.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head>  <title>Конструктор переключателя</title><meta http-equiv="Content-type" content="text/html; charset=utf8"/><meta name="Keywords" content="Конструктор переключателя, конфигуратор переключателя" /><meta name="Description" content="On-line разработка переключателя по техническим требованиям заказчика" /><meta name="Author" content="Дмитрий Коржов" /><link href="/images/icon1.ico" rel="shortcut icon" type="image/x-icon" /><link  rel="stylesheet" type="text/css" href="/styles/styles.css" /></head>  <body>  <?php require_once "../../blocks/header.html";?> <div id = "content" >  <?php require_once "../../blocks/left_panel.html";?>        <div id = "main_content"> <?php if($_COOKIE["construct_mode"] == "edit"): ?><h1>Редактор переключателя</h1><?php else: ?><h1>Конструк